    } catch (error) {
      console.error('Error in postUpload:', error);
      return res.status(500).json({ error: 'Internal Server Error' });
    }
  }

  static async getShow(req, res) {
    try {
      const { 'x-token': token } = req.headers;
      const { id } = req.params;

      // Retrieve the user based on the token
      const userId = await getUserIdFromToken(token);
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      // Convert the ID string to an ObjectId
      const fileId = new ObjectID(id);

      // Retrieve the file document based on ID and user
      const file = await db.collection('files').findOne({ _id: fileId, userId });

      // If no file is found, return a 404 error
      if (!file) {
        return res.status(404).json({ error: 'Not found' });
      }

      // If the file type is 'folder' query the database for files inside this folder
      if (file.type === 'folder') {
        let folderContents;

        if (fileId.equals(0)) {
          // If parentId is 0, it's the root directory
          folderContents = await db.collection('files').find({ parentId: 0, userId }).toArray();
        } else {
          // Query the database for files inside this folder
          folderContents = await db.collection('files').find({ parentId: fileId, userId }).toArray();
        } return res.json(folderContents);
      }

      // If the file type is not 'folder', return the file data
      return res.json(file);
    } catch (error) {
      console.error('Error in getShow:', error);
      return res.status(500).json({ error: 'Internal Server Error' });
    }
  }

  // New endpoint to retrieve user file documents with pagination
  static async getIndex(req, res) {
    try {
      const { 'x-token': token } = req.headers;
      const { parentId, page } = req.query;

      // Retrieve the user based on the token
      const userId = await getUserIdFromToken(token);
      if (!userId) {
